import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  Button,
  Box,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  useScrollTrigger
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { styled } from '@mui/material/styles';
import logoCris from '../assets/logoCris.svg';

// Styled AppBar com prop interna $elevate
const StyledAppBar = styled(AppBar, {
  shouldForwardProp: (prop) => prop !== 'scrolled',
})<{
  scrolled?: boolean;
}>(({ theme, scrolled }) => ({
  backgroundColor: scrolled ? '#566542 !important' : 'rgba(86,101,66,0.0) !important',
  boxShadow: scrolled ? theme.shadows[4] : 'none',
  borderBottom: scrolled ? '1.5px solid #4a5737' : 'none',
  transition: 'background-color 0.3s, box-shadow 0.3s, border-bottom 0.3s',
  zIndex: 2000,
}));

const NavButton = styled(Button)(({ theme }) => ({
  color: 'white',
  margin: '0 10px',
  '&:hover': {
    backgroundColor: '#1B5E20',
  },
  fontWeight: 500,
  letterSpacing: 1,
}));

const Logo = styled('img')({
  height: 56,
  marginRight: 32,
  marginLeft: 16,
});

const navLinks = [
  { label: 'Início', href: '#home' },
  { label: 'Serviços', href: '#services' },
  { label: 'Sobre', href: '#about' },
  { label: 'Contato', href: '#contact' },
];

const Navbar = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 40);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <StyledAppBar position="fixed" scrolled={scrolled} elevation={0}>
      <Toolbar>
        <Logo src={logoCris} alt="Logo PsicoCris" />
        <Box sx={{ flexGrow: 1 }} />
        {/* Menu Desktop */}
        <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center' }}>
          {navLinks.map((item) => (
            <NavButton key={item.href} href={item.href}>{item.label}</NavButton>
          ))}
        </Box>
        {/* Menu Mobile */}
        <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
          <IconButton
            edge="end"
            color="inherit"
            aria-label="menu"
            onClick={() => setDrawerOpen(true)}
            sx={{ ml: 1 }}
          >
            <MenuIcon />
          </IconButton>
          <Drawer
            anchor="right"
            open={drawerOpen}
            onClose={() => setDrawerOpen(false)}
            PaperProps={{ sx: { backgroundColor: '#566542', color: 'white' } }}
          >
            <List sx={{ width: 220 }}>
              {navLinks.map((item) => (
                <ListItem key={item.href} disablePadding>
                  <ListItemButton component="a" href={item.href} onClick={() => setDrawerOpen(false)}>
                    <ListItemText primary={item.label} />
                  </ListItemButton>
                </ListItem>
              ))}
            </List>
          </Drawer>
        </Box>
      </Toolbar>
    </StyledAppBar>
  );
};

export default Navbar;
