import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import { styled } from '@mui/material/styles';

const HeroSection = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, #7A8C6E 60%, #C6A393 100%)',
  padding: theme.spacing(12, 2, 10, 2),
  color: '#fff',
  textAlign: 'center',
  position: 'relative',
  [theme.breakpoints.down('md')]: {
    padding: theme.spacing(8, 2, 6, 2),
  },
}));

const HeroTitle = styled(Typography)({
  fontWeight: 700,
  fontSize: '2.8rem',
  letterSpacing: 1,
  color: '#fff',
  marginBottom: 16,
  textShadow: '0 2px 8px rgba(86,101,66,0.15)',
});

const HeroSubtitle = styled(Typography)({
  fontWeight: 400,
  fontSize: '1.3rem',
  color: '#f5f5f5',
  marginBottom: 32,
});

const HeroButton = styled(Button)({
  background: '#C6A393',
  color: '#fff',
  fontWeight: 700,
  fontSize: '1.1rem',
  borderRadius: 24,
  padding: '12px 32px',
  boxShadow: '0 4px 16px rgba(86,101,66,0.10)',
  '&:hover': {
    background: '#b78f7d',
  },
});

const Hero: React.FC = () => {
  return (
    <HeroSection>
      <HeroTitle variant="h1">
        Psicólogo Clínico | CRP 07/42111
      </HeroTitle>
      <HeroSubtitle>
        Meu propósito é <strong>ajudar você</strong> a ter mais qualidade de vida com saúde mental e emocional.
      </HeroSubtitle>
      <HeroButton
        component="a"
        href="https://wa.me/5551996024420?text=Olá! Gostaria de agendar uma consulta."
        target="_blank"
        rel="noopener noreferrer"
      >
        Agende sua consulta
      </HeroButton>
    </HeroSection>
  );
};

export default Hero;
