import React from 'react';
import { Box, Typography, Card, CardContent } from '@mui/material';
import { styled } from '@mui/material/styles';
import PsychologyIcon from '@mui/icons-material/Psychology';
import PeopleIcon from '@mui/icons-material/People';
import ChildCareIcon from '@mui/icons-material/ChildCare';

const ServicesContainer = styled(Box)(({ theme }) => ({
  padding: '80px 20px',
  backgroundColor: 'white',
}));

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '20px',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-10px)',
  },
}));

const IconWrapper = styled(Box)(({ theme }) => ({
  color: '#dbe5ce',
  fontSize: '48px',
  marginBottom: '20px',
}));

const Services = () => {
  const services = [
    {
      icon: <PsychologyIcon sx={{ fontSize: 48 }} />,
      title: 'Psicoterapia Individual',
      description: 'Caminhos para o autoconhecimento e equilíbrio emocional.',
    },
    {
      icon: <PeopleIcon sx={{ fontSize: 48 }} />,
      title: 'Terapia para Jovens',
      description: 'Apoio emocional para enfrentar desafios da adolescência.',
    },
    {
      icon: <ChildCareIcon sx={{ fontSize: 48 }} />,
      title: 'Psicologia Infantil',
      description: 'Cuidado especializado para o desenvolvimento emocional das crianças.',
    },
  ];

  return (
    <ServicesContainer id="services">
      <Typography variant="h3" component="h2" align="center" gutterBottom sx={{ color: '#1B5E20', mb: 6 }}>
        Nossos Serviços
      </Typography>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 4, justifyContent: 'center' }}>
        {services.map((service, index) => (
          <Box 
            key={index}
            sx={{ 
              flex: { 
                xs: '1 1 100%', 
                sm: '1 1 calc(50% - 16px)', 
                md: '1 1 calc(33.333% - 22px)' 
              },
              maxWidth: { 
                xs: '100%', 
                sm: 'calc(50% - 16px)', 
                md: 'calc(33.333% - 22px)' 
              }
            }}
          >
            <StyledCard>
              <IconWrapper>{service.icon}</IconWrapper>
              <CardContent>
                <Typography variant="h5" component="h3" gutterBottom align="center" sx={{ color: '#2E7D32' }}>
                  {service.title}
                </Typography>
                <Typography variant="body1" align="center" color="text.secondary">
                  {service.description}
                </Typography>
              </CardContent>
            </StyledCard>
          </Box>
        ))}
      </Box>
    </ServicesContainer>
  );
};

export default Services;
