import React from 'react';
import { Box, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';

const HeroSection = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, #566542 60%, #B7C9A8 100%)',
  padding: theme.spacing(18, 2, 16, 2),
  color: '#fff',
  textAlign: 'center',
  position: 'relative',
  [theme.breakpoints.down('md')]: {
    padding: theme.spacing(12, 2, 10, 2),
  },
}));

const HeroTitle = styled(Typography)({
  fontWeight: 700,
  fontSize: '2.8rem',
  letterSpacing: 1,
  color: '#fff',
  marginBottom: 20,
  textShadow: '0 2px 8px rgba(86,101,66,0.15)',
});

const HeroSubtitle = styled(Typography)({
  fontWeight: 400,
  fontSize: '1.5rem',
  color: '#f5f5f5',
  marginBottom: 36,
  maxWidth: 700,
  marginLeft: 'auto',
  marginRight: 'auto',
});

const Hero: React.FC = () => {
  return (
    <HeroSection>
      <HeroTitle variant="h1">
        Psicólogo Clínico | CRP 07/42111
      </HeroTitle>
      <HeroSubtitle>
        Meu propósito é <strong>ajudar você</strong> a ter mais qualidade de vida com saúde mental e emocional.<br />
        Atendimento presencial e online para adultos, adolescentes e crianças.
      </HeroSubtitle>
      <a
        href="https://wa.me/5551996024420?text=Olá! Gostaria de agendar uma consulta."
        target="_blank"
        rel="noopener noreferrer"
        style={{
          display: 'inline-block',
          background: '#566542',
          color: '#fff',
          fontWeight: 700,
          fontSize: '1.1rem',
          borderRadius: 24,
          padding: '14px 38px',
          boxShadow: '0 4px 16px rgba(86,101,66,0.10)',
          textDecoration: 'none',
          marginTop: 8,
          transition: 'background 0.2s',
        }}
        onMouseOver={e => (e.currentTarget.style.background = '#7A8C6E')}
        onMouseOut={e => (e.currentTarget.style.background = '#566542')}
      >
        Agende sua consulta
      </a>
      <svg viewBox="0 0 1440 100" width="100%" height="100" style={{ display: 'block', position: 'absolute', left: 0, bottom: 0, zIndex: 2 }} xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="none">
        <path d="M0,0 C480,100 960,0 1440,100 L1440,100 L0,100 Z" fill="#f5f5f5" />
      </svg>
    </HeroSection>
  );
};

export default Hero;
