import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import { styled } from '@mui/material/styles';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import logoCris from '../assets/logoCris.svg';
import VerifiedIcon from '@mui/icons-material/Verified';

const HeroSection = styled(Box)(({ theme }) => ({
  background: '#566542',
  minHeight: 600,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'relative',
  padding: theme.spacing(12, 2, 0, 2),
  [theme.breakpoints.down('md')]: {
    flexDirection: 'column',
    alignItems: 'center',
    padding: theme.spacing(8, 2, 4, 2),
    minHeight: 480,
  },
}));

const HeroContent = styled(Box)(({ theme }) => ({
  maxWidth: 700,
  zIndex: 2,
  textAlign: 'center',
  padding: theme.spacing(6, 2, 6, 2),
  [theme.breakpoints.down('md')]: {
    padding: theme.spacing(3, 0, 3, 0),
  },
}));

const CRPBadge = styled(Box)(({ theme }) => ({
  display: 'inline-flex',
  alignItems: 'center',
  gap: 8,
  color: '#a97c50',
  borderRadius: 12,
  fontWeight: 800,
  fontSize: 20,
  px: 5,
  py: 1.5,
  boxShadow: '0 2px 12px rgba(86,101,66,0.10)',
  letterSpacing: 1,
  mb: 2,
}));

const LogoDecorativo = styled('img')(({ theme }) => ({
  position: 'absolute',
  right: 60,
  top: '50%',
  transform: 'translateY(-50%)',
  width: 220,
  opacity: 0.10,
  zIndex: 1,
  pointerEvents: 'none',
  [theme.breakpoints.down('md')]: {
    display: 'none',
  },
}));

const Hero = () => {
  return (
    <HeroSection>
      <HeroContent>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 2, mb: 2, flexWrap: 'wrap' }}>
          <Typography variant="h2" sx={{ color: '#fff', fontWeight: 800, letterSpacing: 1, fontFamily: 'serif', mb: 0 }}>
            Cristian Hendler
          </Typography>
          <CRPBadge>
            <VerifiedIcon sx={{ color: '#a97c50', fontSize: 22, mr: 0.5 }} />
            CRP 07/42111
          </CRPBadge>
        </Box>
        <Typography sx={{ color: '#e9e6e1', fontSize: 22, fontWeight: 500, mb: 4, mt: 2 }}>
          <Box component="span" sx={{ whiteSpace: 'nowrap' }}>
            Atendimento presencial em Torres/RS e online para todo o Brasil.
          </Box>
        </Typography>
        <Button
          variant="contained"
          sx={{
            background: '#7a5430',
            color: '#fff',
            fontWeight: 700,
            fontSize: 20,
            px: 6,
            py: 2,
            borderRadius: 8,
            boxShadow: '0 8px 32px rgba(86,101,66,0.18)',
            mt: 3,
            '&:hover': { background: '#5c4031' },
          }}
          startIcon={<WhatsAppIcon />}
          href="https://wa.me/5551996024420?text=OlÃ¡! Gostaria de agendar uma consulta."
          target="_blank"
        >
          Agende sua consulta
        </Button>
      </HeroContent>
      <LogoDecorativo src={logoCris} alt="Logo PsicoCris" />
      <svg viewBox="0 0 1440 100" width="100%" height="100" style={{ display: 'block', position: 'absolute', left: 0, bottom: 0, zIndex: 2 }} xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="none">
        <path d="M0,0 C480,100 960,0 1440,100 L1440,100 L0,100 Z" fill="#f5f5f5" />
      </svg>
    </HeroSection>
  );
};

export default Hero;
