import React from 'react';
import { Box, Typography, Container, Grid, IconButton, Link } from '@mui/material';
import { styled } from '@mui/material/styles';
import InstagramIcon from '@mui/icons-material/Instagram';
import BannerImage from '../assets/bannerr.svg';

const AboutContainer = styled(Box)(({ theme }) => ({
  padding: '80px 20px',
  backgroundColor: '#f5f5f5',
}));

const ProfileImage = styled('img')({
  width: 320,
  height: 320,
  objectFit: 'cover',
  borderRadius: '28% 72% 70% 30% / 30% 30% 70% 70%',
  boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
  background: '#fff',
  display: 'block',
  margin: '0 auto',
});

const About = () => {
  return (
    <AboutContainer id="about">
      <Container maxWidth="lg">
        <Grid container spacing={6} alignItems="center" justifyContent="center">
          <Grid item xs={12} md={5}>
            <ProfileImage src={BannerImage} alt="Psicólogo Cristian Hendler" />
          </Grid>
          <Grid item xs={12} md={7}>
            <Typography variant="h4" component="h2" sx={{ color: '#6d5c7c', fontWeight: 700 }} gutterBottom>
              Psicólogo Cristian Hendler<br />
              <span style={{ fontWeight: 500, fontSize: 28 }}>CRP 07/42111</span>
            </Typography>
            <Box sx={{ mb: 2 }}>
              <IconButton
                component={Link}
                href="https://www.instagram.com/psicocrishendler/"
                target="_blank"
                rel="noopener noreferrer"
                sx={{ color: '#e1306c', background: '#fff', mb: 1 }}
                aria-label="Instagram"
              >
                <InstagramIcon fontSize="large" />
              </IconButton>
            </Box>
            <Typography paragraph sx={{ fontSize: 18 }}>
              Sou Psicólogo Clínico formado pela Universidade Luterana do Brasil (ULBRA) - Torres/RS, com registro no Conselho Regional de Psicologia do Rio Grande do Sul (CRP 07/42111).
            </Typography>
            <Typography paragraph sx={{ fontSize: 18 }}>
              Atuo na área clínica, oferecendo atendimento psicológico individual para adultos, adolescentes e crianças, com foco em questões emocionais, comportamentais e desenvolvimento pessoal.
            </Typography>
            <Typography paragraph sx={{ fontSize: 18 }}>
              Ofereço um espaço seguro e acolhedor para que você possa explorar suas questões emocionais, desenvolver autoconhecimento e estratégias para lidar com os desafios da vida.
            </Typography>
            <Typography paragraph sx={{ fontSize: 18 }}>
              Especialista em atendimento presencial em Torres/RS e online para todo o Brasil e brasileiros no exterior.
            </Typography>
          </Grid>
        </Grid>
      </Container>
    </AboutContainer>
  );
};

export default About;
